(claws := list(map(int, __import__("re").findall(r'\d+', open(0).read()))), claws := [claws[i:i+6] for i in range(0, len(claws), 6)], print(sum((new_x := 10000000000000 + x, new_y := 10000000000000 + y, a := (y_b*new_x - x_b*new_y) / (y_b*x_a - x_b*y_a), b := (new_x - x_a*a) / x_b, 3*int(a) + int(b) if a.is_integer() and b.is_integer() else 0)[-1] for x_a, y_a, x_b, y_b, x, y in claws)))
